options.customCss:
/* ===== Tema Dark Futurista / Neon ===== */
:root {
  --bg-0: #0b0f14;
  --bg-1: #0f141a;
  --bg-2: #111720;
  --txt-0: #e6f4ea;
  --txt-1: #a8b3ad;
  --neon: #39ff14;          /* verde neon */
  --neon-2: #00f5ff;        /* ciano neon */
  --danger: #ff3b3b;
  --ring: rgba(57, 255, 20, 0.35);
  --shadow-neon: 0 0 12px rgba(57,255,20,.25), 0 0 32px rgba(0,245,255,.15);
}

body {
  background:
    radial-gradient(1200px 600px at 80% -20%, rgba(57,255,20,.06), transparent 60%),
    radial-gradient(1000px 500px at -10% 80%, rgba(0,245,255,.05), transparent 60%),
    linear-gradient(180deg, var(--bg-0), var(--bg-1));
  min-height: 100dvh;
  color: var(--txt-0);
  font-family: 'Open Sans', system-ui, -apple-system, Segoe UI, Roboto, sans-serif;
}

/* Container com borda neon animada (glass + glow) */
.form-nano-banana {
  width: min(100%, 520px);
  margin: 32px auto;
  position: relative;
  background: linear-gradient(180deg, rgba(255,255,255,.02), rgba(255,255,255,.01));
  border: 1px solid rgba(57,255,20,.25);
  border-radius: 14px;
  padding: 28px;
  box-shadow: var(--shadow-neon);
  backdrop-filter: blur(8px);
  overflow: hidden;
}

.form-nano-banana::before {
  content: "";
  position: absolute;
  inset: -2px;
  border-radius: 16px;
  background: conic-gradient(from 120deg,
    transparent 0deg,
    rgba(57,255,20,.25) 90deg,
    rgba(0,245,255,.25) 180deg,
    transparent 270deg);
  filter: blur(12px);
  opacity: .35;
  z-index: -1;
  animation: spin 6s linear infinite;
}
@keyframes spin { to { transform: rotate(360deg); } }

/* Cabeçalho */
.form-nano-banana h1 {
  font-size: 28px;
  line-height: 1.1;
  margin: 0 0 8px;
  letter-spacing: .5px;
  text-shadow: 0 0 10px rgba(57,255,20,.35);
}

.form-nano-banana .sub {
  color: var(--txt-1);
  font-size: 14px;
  margin-bottom: 22px;
}

/* Grid dos campos */
.form-nano-banana .grid {
  display: grid;
  gap: 16px;
}

/* Label com detalhe neon */
.form-nano-banana label {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-size: 13px;
  color: var(--txt-1);
  text-transform: uppercase;
  letter-spacing: .08em;
}
.form-nano-banana label::before {
  content: "";
  width: 6px; height: 6px;
  border-radius: 2px;
  background: linear-gradient(90deg, var(--neon), var(--neon-2));
  box-shadow: 0 0 8px rgba(57,255,20,.8);
}

/* Inputs / Select / Textarea */
.form-nano-banana input,
.form-nano-banana select,
.form-nano-banana textarea {
  width: 100%;
  background: linear-gradient(180deg, rgba(255,255,255,.03), rgba(255,255,255,.01));
  border: 1px solid rgba(255,255,255,.08);
  border-radius: 10px;
  padding: 12px 14px;
  font-size: 14px;
  color: var(--txt-0);
  outline: none;
  transition: border .15s ease, box-shadow .15s ease, transform .05s ease;
}

.form-nano-banana input::placeholder,
.form-nano-banana textarea::placeholder {
  color: rgba(255,255,255,.35);
}

.form-nano-banana input:focus,
.form-nano-banana select:focus,
.form-nano-banana textarea:focus {
  border-color: var(--neon);
  box-shadow: 0 0 0 3px var(--ring), inset 0 0 18px rgba(57,255,20,.08);
  transform: translateZ(0) scale(1.005);
}

/* Select com seta custom */
.form-nano-banana select {
  appearance: none;
  background-image:
    linear-gradient(45deg, transparent 50%, var(--neon) 50%),
    linear-gradient(135deg, var(--neon) 50%, transparent 50%);
  background-position: calc(100% - 18px) 54%, calc(100% - 12px) 54%;
  background-size: 6px 6px, 6px 6px;
  background-repeat: no-repeat;
}

/* Linha sutil entre seções */
.form-nano-banana .divider {
  height: 1px;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,.1), transparent);
  margin: 4px 0 12px;
}

/* Botão primário neon */
.form-nano-banana .btn {
  position: relative;
  width: 100%;
  height: 48px;
  border: 0;
  border-radius: 12px;
  cursor: pointer;
  color: #0b0f14;
  font-weight: 700;
  font-size: 15px;
  letter-spacing: .02em;
  background: linear-gradient(90deg, var(--neon), var(--neon-2));
  box-shadow: 0 0 18px rgba(57,255,20,.35), 0 0 28px rgba(0,245,255,.2);
  transition: transform .08s ease, box-shadow .15s ease, filter .15s ease;
}
.form-nano-banana .btn:hover {
  transform: translateY(-1px);
  box-shadow: 0 0 22px rgba(57,255,20,.45), 0 0 34px rgba(0,245,255,.28);
  filter: saturate(1.1);
}
.form-nano-banana .btn:active {
  transform: translateY(0);
}

/* Avisos/erros */
.form-nano-banana .error {
  color: var(--danger);
  font-size: 12px;
  margin-top: -8px;
  text-shadow: 0 0 8px rgba(255,59,59,.25);
}

/* Rodapé mini-infos */
.form-nano-banana .meta {
  margin-top: 14px;
  font-size: 12px;
  color: var(--txt-1);
  display: flex;
  align-items: center;
  gap: 10px;
}
.form-nano-banana .pill {
  border: 1px dashed rgba(57,255,20,.45);
  color: var(--neon);
  padding: 4px 8px;
  border-radius: 999px;
  font-weight: 700;
  letter-spacing: .05em;
}
